package flowcontrolgroups;

public class DoWhileDemo {

	    
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//print first n natural numbers
		int i = 1;
		do {

			//body of loop
	System.out.print("this is the number: " + i);
			//expression
			i++;
			
			System.out.print("\n");
		}while(i <= 20);
		{

		return;
		}
	}
	}
